pragma solidity ^0.4.20;

// importing the YakhiToken
import "./YakhiToken.sol";

contract YakhiTokenSale {
    // Keep track of external account with special privileges - state variable for admin
    // State variable written to disk, belongs to contract and is deployed to blockchain
    // Notice that admin isn't public as we don't want to expose the address of the admin, so
    // we dont test for the correct admin address, just that it exists
    address admin;
    // State variable to connect with token contract
    // This is going to be a contract data type
    YakhiToken public tokenContract;
    // State variable to implement the token price
    uint256 public tokenPrice; 
    // Keep track of the number of tokens sold
    uint256 public tokensSold;
    // Declare the Sell event
    event Sell(address _buyer, uint256 _amount);
    // Constructor function for the TokenSale
    // Here, we pass the address of the YakhiToken to get a reference to it and assign it to the
    // state variable
    function YakhiTokenSale(YakhiToken _tokenContract, uint256 _tokenPrice) public {
        // Assign an admin, which is an external account to this token sale that has extra added
        // privileges (example, only admin can end a token contract)
        // msg.sender is the person who calls the function, or deploys the contract
        admin = msg.sender;
        // Interact with the token contract
        // We want to be able to purchase tokens, so we need to add the token contract in the token
        // sale. We'll rely on the transfer function whenever we need to actually buy/sell things. We
        // transfer this from the admin we just assigned
        tokenContract = _tokenContract;
        // Set the token price
        tokenPrice = _tokenPrice;
    }

    // Using an external library's multiply function
    // Internal visibility means it's internal in the contract, can't be called outside
    // pure means it takes in same data type and returns same data type
    function multiply(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x);
    }

    // Buying tokens
    // We declare this as public to expose this to the public interface, and we declare it 
    // as payable to allow people to actually send Ether with this contract
    // When we call this, we specify the number of tokens to buy. The function metadata requires
    // the account that calls this, and the amount of ether that's being sent
    function buyTokens(uint256 _numberOfTokens) public payable {
        // Require that the value is equal to tokens
        // msg.value is metadata that we get when the function is called, which is the amount of
        // Wei that the function gets
        require(msg.value == multiply(_numberOfTokens, tokenPrice));
        // Require that there are enough tokens in the contract
        // Need to make sure that the balance of the tokenSale is more than value to buy
        require(tokenContract.balanceOf(this) >= _numberOfTokens);
        // To do this, we need to provision tokens to the token sale contract - just as wallets can
        // have tokens, TokenSale and general contracts can also have tokens. We do this as it has an
        // address, and we can add some to its balanceOf function. We do this by transferring some tokens
        // to the smart contract, rather than account. The basic idea is we want to take an amount of
        // the total supply of tokens and give it to this contract, and sell these tokens
        // Require that the transfer is successful
        require(tokenContract.transfer(msg.sender, _numberOfTokens));
        // Keep track of number of tokensSold - increase it by number of tokens bought
        tokensSold += _numberOfTokens;
        // Trigger a sell event
        // The buyer here is whoever triggers this buyTokens() event
        Sell(msg.sender, _numberOfTokens);
    }

    // End Sale function for ending a YakhiTokenSale
    // Require that only an admin can do this, this is when ending a sale. We want to make sure we
    // transfer the remaining amount of tokens in the sale back to the admin
    function endSale() public {
        // Require admin is the msg.sender, person who invokes function
        require(msg.sender == admin);
        // We call the transfer() function and transfer to the admin all the remaining balance of
        // this smart sale contract
        // Transfer remaining YakhiTokens back to admin
        require(tokenContract.transfer(admin, tokenContract.balanceOf(this)));
        // Destroy contract - admin does self destruct
        selfdestruct(admin);
    }
}
